import React, { useEffect } from "react";
import {useSelector, useDispatch} from 'react-redux';
import { getTodo , todosSelector,removeTodos, removeTodo, markComplete} from '../Store/Reducer/todoSlice';
import {ListGroup, Button} from 'react-bootstrap'


function Todos() {
  const todos = useSelector(todosSelector);

  const dispatch = useDispatch();

  const toggleIsComplete = (id) => {
    dispatch(markComplete(id))
  }

  const handleDelete = (id) => {
    dispatch(removeTodo(id));
		dispatch(getTodo());
  }

  useEffect(() => {
		// send request to jsonplaceholder
		dispatch(getTodo())
	}, [dispatch])

  return (
    <div>
      <ListGroup className="Todos-list">
        {todos.map((todo) => (
          <ListGroup.Item key={todo.id} className="Todos-item">
            <span>{todo.title}</span>
            <input key={todo.id} 
            type="checkbox" 
            checked={todo.isComplete} 
            onChange={toggleIsComplete.bind(this, todo.id)}/>
            <Button variant='primary' onClick={handleDelete.bind(this, todo.id)}>
              Delete
            </Button>
          </ListGroup.Item>
        ))}
      </ListGroup>
    </div>
  );
}

export default Todos;
