import React from 'react';
import {ListGroup, Button, Container, Row, Col} from 'react-bootstrap'


const TodoItem = (props) => {

    return (
          <ListGroup.Item as='li' className="Todos-item">
            <Container>
            <Row>
              <Col>
                {props.todo.title}
              </Col>
              <Col xs lg="1"  md="auto">
                <input key={props.todo.id} 
                type="checkbox" 
                checked={props.todo.isComplete} 
                onChange={props.toggleIsComplete.bind(this, props.todo.id)}/>
              </Col>
              <Col xs lg="1">
                <Button variant='danger' onClick={props.handleDelete.bind(this, props.todo.id)}>
                  Delete
                </Button>
              </Col>
            </Row>
            </Container>
          </ListGroup.Item>
    );
};

export default TodoItem;
